{-|
  The directions copy/paste onto separate lines so I took the liberty of making
  them into a List of Strings.

  The basic idea is that each String will produce a digit in the final code.
  Use foldl to walk the List and map each value to its digit.
  Two details to note:
  1. The list is built in reverse because it makes pattern matching easier in the fold
  2. The starting position is "5" and needs to be removed before reporting the final answer.
-}
module AdventDay2 where

import Data.Char

getOneDigit :: Int -> String -> Int
getOneDigit start [] = start
getOneDigit start ('U':xs) = if start `elem` [1,2,3] then getOneDigit start xs else getOneDigit (start-3) xs
getOneDigit start ('R':xs) = if start `elem` [3,6,9] then getOneDigit start xs else getOneDigit (start+1) xs
getOneDigit start ('D':xs) = if start `elem` [7,8,9] then getOneDigit start xs else getOneDigit (start+3) xs
getOneDigit start ('L':xs) = if start `elem` [1,4,7] then getOneDigit start xs else getOneDigit (start-1) xs

directions =
  [ ""
  , ""
  , ""
  , ""
  , ""
  ]

solve :: String
solve = map intToDigit . tail . reverse $ foldl (\(x:xs) y -> (getOneDigit x y):x:xs) [5] directions
